    setSearchField(e.target.value);
  };

  const submit = (event) => {
    event.preventDefault();
    const newblog = {
      search: searchField,

    };}




  useEffect(() => {
    let fetchoption = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(),
    };

    fetch("http://localhost:3000/", fetchoption)
      .then((response) => response.json())
      .then((json) => setItems(json.files));
