  return (
    <div className="App">
      <form class="example" action="action_page.php">
        <input
          type="text"
          placeholder="Search.."
          name="search"
          onChange={handleChange}
        />
        <button type="submit">submit</button>
      </form>

      <h1>{resourceType}</h1>
      <>
        {items.map((item) => {
          return <pre>{JSON.stringify(item)}</pre>;
        })}
      </>
    </div>
  );
}

export default App;
